import { useState } from "react";
import { useDispatch } from "react-redux";
import { addTransaction, fetchTransactions } from "../redux/transactionSlice";

export default function TransactionForm() {
  const [form, setForm] = useState({ type: "expense", amount: "", category: "", description: "" });
  const dispatch = useDispatch();

  const handleSubmit = async (e) => {
    e.preventDefault();
    await dispatch(addTransaction({ ...form, date: new Date() }));
    dispatch(fetchTransactions());
    setForm({ type: "expense", amount: "", category: "", description: "" });
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-gray-100 rounded shadow">
      <select name="type" value={form.type} onChange={e => setForm({...form, type: e.target.value})}>
        <option value="expense">Expense</option>
        <option value="income">Income</option>
      </select>
      <input type="number" placeholder="Amount" value={form.amount}
        onChange={e => setForm({...form, amount: e.target.value})} required />
      <input type="text" placeholder="Category" value={form.category}
        onChange={e => setForm({...form, category: e.target.value})} required />
      <input type="text" placeholder="Description" value={form.description}
        onChange={e => setForm({...form, description: e.target.value})} />
      <button type="submit" className="bg-blue-500 text-white p-2 rounded">Add</button>
    </form>
  );
}
