import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchTransactions } from "../redux/transactionSlice";
import TransactionForm from "../components/TransactionForm";
import SummaryChart from "../components/SummaryChart";

export default function Dashboard() {
  const dispatch = useDispatch();
  const { items, status } = useSelector(state => state.transactions);

  useEffect(() => { dispatch(fetchTransactions()); }, [dispatch]);

  return (
    <div className="p-6 space-y-4">
      <h1 className="text-2xl font-bold">Expense Tracker</h1>
      <TransactionForm />
      <SummaryChart />
      <div className="bg-white p-4 rounded shadow">
        <h2 className="text-lg font-semibold mb-2">Transactions</h2>
        {status === "loading" ? (
          <p>Loading...</p>
        ) : (
          <ul>
            {items.map(t => (
              <li key={t._id} className="border-b py-1">
                <strong>{t.category}</strong> - {t.type} â‚¹{t.amount} ({new Date(t.date).toLocaleDateString()})
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
