import { useState } from "react";
import { useDispatch } from "react-redux";
import { addTransaction, fetchTransactions } from "../redux/transactionSlice";
import toast from "react-hot-toast";

export default function TransactionForm() {
  const [form, setForm] = useState({
    type: "expense",
    amount: "",
    category: "",
    description: "",
  });
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  const validateForm = () => {
    if (!form.amount || form.amount <= 0) {
      toast.error("Please enter amount ");
      return false;
    }
    if (!form.category.trim()) {
      toast.error("Enter Category");
      return false;
    }
    if (!form.type) {
      toast.error("Please select a transaction type.");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      setLoading(true);
      await dispatch(addTransaction({ ...form, date: new Date() })).unwrap();
      await dispatch(fetchTransactions());
      setForm({ type: "expense", amount: "", category: "", description: "" });
      toast.success("Transaction added successfully ðŸŽ‰");
    } catch (err) {
      toast.error("Failed to add transaction. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white/90 backdrop-blur-sm p-6 rounded-xl shadow-md border border-gray-200 w-full">
      <h2 className="text-lg font-semibold mb-4 text-gray-700">Add New Transaction</h2>
      <form onSubmit={handleSubmit} className="grid lg:grid-cols-2 gap-4">
        <select
          value={form.type}
          onChange={(e) => setForm({ ...form, type: e.target.value })}
          className="p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-500"
        >
          <option value="expense">Expense</option>
          <option value="income">Income</option>
        </select>

        <input
          type="number"
          placeholder="Amount"
          value={form.amount}
          onChange={(e) => setForm({ ...form, amount: e.target.value })}
          
          className="p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-500"
        />

        <input
          type="text"
          placeholder="Category"
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
          
          className="p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-500"
        />

        <input
          type="text"
          placeholder="Description"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          className="p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-500 col-span-2"
        />

        <button
          type="submit"
          disabled={loading}
          className={`col-span-2 bg-indigo-600 text-white py-3 rounded-lg font-medium transition ${
            loading ? "opacity-60 cursor-not-allowed" : "hover:bg-indigo-700"
          }`}
        >
          {loading ? "Adding..." : "+ Add Transaction"}
        </button>
      </form>
    </div>
  );
}
