import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchTransactions } from "../redux/transactionSlice";
import TransactionForm from "../components/TransactionForm";
import SummaryChart from "../components/SummaryChart";

export default function Dashboard() {
  const dispatch = useDispatch();
  const { items, status } = useSelector(state => state.transactions);

  useEffect(() => { dispatch(fetchTransactions()); }, [dispatch]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-white flex flex-col items-center py-8 px-4">
      <div className="max-w-5xl w-full space-y-8">
        <h1 className="text-3xl font-bold text-center text-gray-800">
          ðŸ’° Expense Tracker Dashboard
        </h1>

        {/* Top Section */}
        <div className="grid md:grid-cols-2 gap-6">
          <TransactionForm />
          <SummaryChart />
        </div>

        {/* Transaction List */}
        <div className="bg-white/80 backdrop-blur-sm p-6 rounded-xl shadow-sm border border-gray-200">
          <h2 className="text-lg font-semibold mb-4 text-gray-700">Recent Transactions</h2>
          {status === "loading" ? (
            <p className="text-gray-500">Loading...</p>
          ) : items.length === 0 ? (
            <p className="text-gray-400">No transactions yet.</p>
          ) : (
            <ul className="divide-y divide-gray-200">
              {items.map(t => (
                <li key={t._id} className="flex justify-between py-3 hover:bg-gray-50 transition">
                  <div>
                    <p className="font-medium text-gray-800">{t.category}</p>
                    <p className="text-sm text-gray-500">{t.description || "No description"}</p>
                  </div>
                  <div className="text-right">
                    <p
                      className={`font-semibold ${
                        t.type === "income" ? "text-green-500" : "text-red-500"
                      }`}
                    >
                      {t.type === "income" ? "+" : "-"}â‚¹{t.amount}
                    </p>
                    <p className="text-xs text-gray-400">{new Date(t.date).toLocaleDateString()}</p>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}
