import { useState } from "react";
import { useDispatch } from "react-redux";
import { addTransaction, fetchTransactions } from "../redux/transactionSlice";

export default function TransactionForm() {
  const [form, setForm] = useState({ type: "expense", amount: "", category: "", description: "" });
  const dispatch = useDispatch();

  const handleSubmit = async (e) => {
    e.preventDefault();
    await dispatch(addTransaction({ ...form, date: new Date() }));
    dispatch(fetchTransactions());
    setForm({ type: "expense", amount: "", category: "", description: "" });
  };

  return (
    <div className="bg-white/80 backdrop-blur-sm p-6 rounded-xl shadow-sm border border-gray-200">
      <h2 className="text-lg font-semibold mb-4 text-gray-700">Add Transaction</h2>
      <form onSubmit={handleSubmit} className="grid sm:grid-cols-2 gap-4">
        <select
          name="type"
          value={form.type}
          onChange={e => setForm({ ...form, type: e.target.value })}
          className="p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-500"
        >
          <option value="expense">Expense</option>
          <option value="income">Income</option>
        </select>

        <input
          type="number"
          placeholder="Amount"
          value={form.amount}
          onChange={e => setForm({ ...form, amount: e.target.value })}
          required
          className="p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-500"
        />

        <input
          type="text"
          placeholder="Category"
          value={form.category}
          onChange={e => setForm({ ...form, category: e.target.value })}
          required
          className="p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-500"
        />

        <input
          type="text"
          placeholder="Description"
          value={form.description}
          onChange={e => setForm({ ...form, description: e.target.value })}
          className="p-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-indigo-500 col-span-2"
        />

        <button
          type="submit"
          className="col-span-2 bg-indigo-600 text-white font-medium py-3 rounded-lg hover:bg-indigo-700 transition duration-200"
        >
          + Add Transaction
        </button>
      </form>
    </div>
  );
}
